con:config.script:
import groovy.json.JsonSlurper

def response = testRunner.testCase.getTestStepByName("REST Request - Add_New_Customer").getPropertyValue("response")
//log.info response

def holder = new JsonSlurper().parseText(response)

log.info holder.data.id
log.info holder.success
log.info holder.error

def placeid = holder.data.id
log.info placeid
context.testCase.getTestStepByName("custid").setPropertyValue("custid", placeid.toString())

