import java.io.*
import jxl.*
import jxl.write.*
import java.text.SimpleDateFormat

// i want to set my authorization in the header 

def accessToken = "20ec0ccb87f375b78c473074bff400a92ff3d42f";

def file = new File("C:\\Users\\AnushaUmrey\\Documents\\Project\\sampleGetCustomerByIdData.xls");

def workbook = Workbook.getWorkbook(file)

def workSheet = workbook.getSheet("Sheet1")

rows = workSheet.getRows()

log.info "Total Number of rows are: " +rows

for(def i=1;i<rows;i++)
{
	Cell c = workSheet.getCell(0,i)
	firstname = c.getContents()
	log.info ("Id is " +firstname);

	c = workSheet.getCell(1,i)
	lastname = c.getContents()
	log.info ("Id is " +lastname);

	c = workSheet.getCell(2,i)
	email = c.getContents()
	log.info ("Id is " +email);


}

def tc = testRunner.testCase.testSuite.project.testSuites["TestSuite 1"].testCases["TC04-AddNewCustomer_Filter02_DataDriven"]
tc.testSteps["Properties"].setPropertyValue("email", email)
tc.testSteps["Properties"].setPropertyValue("firstname", firstname)
tc.testSteps["Properties"].setPropertyValue("lastname", lastname)

tc.run(null, false)

